                  const  localStorageItem = localStorage.getItem(itemName);
                  let parsedItem;

                if (!localStorageItem) {
                          localStorage.setItem(itemName, JSON.stringify(initialValue));
                          parsedItem = initialValue;

                } else {
                            parsedItem = JSON.parse(localStorageItem);
                }
                const  [item, setItem] =React.useState(parsedItem);
                const saveItem = (newItem) => {
                  localStorage.setItem(itemName,JSON.stringify(newItem));
                  setItem(newItem);
            };
            return  [item, saveItem];
}

function App() {


  /* Create status  */
  const [shoppingList, setShoppingList]  = React.useState(parsedFoods );
  const [searchValue, setSearchValue] = React.useState('');

const buyFood = shoppingList.filter(
  shop => !!shop.completed)
  .length;
const allFood = shoppingList.length;

const searchedFoods = shoppingList.filter(
  (shop) => {
    const shopText =shop.text.toLowerCase();
    const searchText = searchValue.toLocaleLowerCase();
    return  shopText.includes(searchText);
  } 
);



  const buyedFood = (text) => {
            const newList = [...shoppingList];
            const newIndex =newList.findIndex(
              (shop) => shop.text === text
            );
            newList[newIndex].completed = true;
            saveFoods(newList);
};

const deleteFood = (text) => {
  const newList = [...shoppingList];
  const newIndex =newList.findIndex(
    (shop) => shop.text === text
  );
  newList.splice(newIndex, 1);
  saveFoods(newList);
};

  return (
    <React.Fragment>
      <TodoCounter 
        completed={buyFood} 
        total={allFood}
      />
      <TodoSearch 
            searchValue={searchValue}  
            setSearchValue={setSearchValue}
      />

      <TodoList>
          {searchedFoods.map( shop => (
            <TodoItem 
              key={shop.text} 
              text={shop.text}
              completed ={shop.completed}
              onComplete ={() => buyedFood(shop.text)}
              onDelete={()=> deleteFood(shop.text)}
               />
          ))}
      </TodoList>

      <CreateTodoButton/>

    </React.Fragment>


  );
}

export default App;
