// localStorage.removeItem('FOOD_V1');


function App() {

  const  localStorageFoods = localStorage.getItem('FOOD_V1');
  let parsedFoods;

if (!localStorageFoods) {
          localStorage.setItem('FOOD_V1', JSON.stringify([]));
          parsedFoods = [];

} else {

}

  /* Create status  */
  const [shoppingList, setShoppingList]  = React.useState(parsedFoods );
  const [searchValue, setSearchValue] = React.useState('');

const buyFood = shoppingList.filter(
  shop => !!shop.completed)
  .length;
const allFood = shoppingList.length;

const searchedFoods = shoppingList.filter(
  (shop) => {
    const shopText =shop.text.toLowerCase();
    const searchText = searchValue.toLocaleLowerCase();
    return  shopText.includes(searchText);
  } 
);

  const buyedFood = (text) => {
            const newList = [...shoppingList];
            const newIndex =newList.findIndex(
              (shop) => shop.text === text
            );
            newList[newIndex].completed = true;
            setShoppingList(newList);
};

const deleteFood = (text) => {
  const newList = [...shoppingList];
  const newIndex =newList.findIndex(
    (shop) => shop.text === text
  );
  newList.splice(newIndex, 1);
  setShoppingList(newList);
};

  return (
    <React.Fragment>
      <TodoCounter 
        completed={buyFood} 
        total={allFood}
      />
      <TodoSearch 
            searchValue={searchValue}  
            setSearchValue={setSearchValue}
      />

      <TodoList>
          {searchedFoods.map( shop => (
            <TodoItem 
              key={shop.text} 
              text={shop.text}
              completed ={shop.completed}
              onComplete ={() => buyedFood(shop.text)}
              onDelete={()=> deleteFood(shop.text)}
               />
          ))}
      </TodoList>

      <CreateTodoButton/>

    </React.Fragment>


  );
}

export default App;
